CREATE TABLE sensor_data (
  ID_Lectura VARCHAR(255),
  Fecha_Hora VARCHAR(255),
  ID_Sensor VARCHAR(255),
  Tipo_Medicion VARCHAR(255),
  Unidad_Medida VARCHAR(255),
  Valor_Lectura INTEGER,
  ID_Estacion VARCHAR(255),
  Nombre_Estacion VARCHAR(255),
  Tipo_Zona VARCHAR(255),
  Pais VARCHAR(255),
  Latitud DECIMAL(10, 2),
  Longitud DECIMAL(10, 2),
  ID_Proyecto VARCHAR(255),
  Nombre_Proyecto VARCHAR(255),
  Investigador_Principal VARCHAR(255)
);

